mapper.update:
update `order` set
product_id = #{productId}, product_num = #{productNum}, price = #{price}, buyer_id = #{buyerId}, seller_id = #{sellerId}, buyer_addr = #{buyerAddr}, seller_addr = #{sellerAddr}, logistic_id = #{logisticId}, `status` = #{status}
where order_id = #{orderId}

mapper.select:
select order_id as orderId, `order`.product_id as productId, product_num as productNum, product_name as productName, price as price, buyer_id as buyerId, seller_id as sellerId, order_time as orderTime, buyer_addr as buyerAddr, (select CONCAT(province,city,county,address_detail) addressname from address where address_id=buyerAddr) buyerAddrName, seller_addr as sellerAddr, `order`.logistic_id as logisticId, com_name as comName, `status` as status
from `order` , logistic_com, (select DISTINCT product.product_name,product.product_id from product,`order` where product.product_id = `order`.product_id) as pp
where `order`.product_id=pp.product_id and `order`.logistic_id=logistic_com.logistic_id and seller_id = #{sellerId} limit #{page},#{size}
