mapper.insert:
insert into
product(seller_id, product_type_id,
product_name,
product_image, price, discount_price,
storage, sale, state,
create_time,modify_time,product_describe)

values(#{sellerId}, #{productTypeId} , #{productName}
, #{productImage}, #{price}, #{discountPrice}
, #{storage}, #{sale}, #{state}
, #{createTime}, #{modifyTime},#{productDescribe} )

mapper.select:
select count(*) from
product where 1=1
and seller_id = #{sellerId}

select product_id as productId, seller_id as sellerId, product.product_type_id as productTypeId, product_type_name as productTypeName, product_name as productName, product_image as productImage, price as price, discount_price as discountPrice, storage , sale , state , create_time as createTime, modify_time as modifyTime, product_describe as productDescribe from product left join product_type on product.product_type_id = product_type.product_type_id
product.product_type_id = #{productTypeId}
and seller_id = #{sellerId}
limit #{page},#{size}
